import React, { useEffect, useState } from "react";
import "./App.css";

// import * as React from "react";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useTheme } from "@mui/material/styles";

const ResponsiveDialog = () => {
  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down("md"));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <React.Fragment>
      <Button variant="outlined" onClick={handleClickOpen}>
        Open responsive dialog
      </Button>
      <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">
          {"Use Google's location service?"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Let Google help apps determine location. This means sending
            anonymous location data to Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose}>
            Disagree
          </Button>
          <Button onClick={handleClose} autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
};

const SIZE = 4;

const generateEmptyGrid = () => {
  return Array(SIZE)
    .fill()
    .map(() => Array(SIZE).fill(0));
};

const addRandomTile = (grid) => {
  const emptyCells = [];
  for (let r = 0; r < SIZE; r++) {
    for (let c = 0; c < SIZE; c++) {
      if (grid[r][c] === 0) emptyCells.push([r, c]);
    }
  }
  if (emptyCells.length === 0) return grid;
  const [r, c] = emptyCells[Math.floor(Math.random() * emptyCells.length)];
  grid[r][c] = Math.random() < 0.9 ? 2 : 4;
  return grid;
};

const isGameOver = (grid) => {
  for (let r = 0; r < SIZE; r++) {
    for (let c = 0; c < SIZE; c++) {
      if (grid[r][c] === 0) return false;
      if (c < SIZE - 1 && grid[r][c] === grid[r][c + 1]) return false;
      if (r < SIZE - 1 && grid[r][c] === grid[r + 1][c]) return false;
    }
  }
  return true;
};

const transpose = (grid) => grid[0].map((_, c) => grid.map((row) => row[c]));
const reverse = (grid) => grid.map((row) => [...row].reverse());

const operateRow = (row) => {
  let scoreGained = 0;
  let newRow = row.filter((val) => val !== 0);
  for (let i = 0; i < newRow.length - 1; i++) {
    if (newRow[i] === newRow[i + 1]) {
      newRow[i] *= 2;
      scoreGained += newRow[i];
      newRow[i + 1] = 0;
    }
  }
  newRow = newRow.filter((val) => val !== 0);
  while (newRow.length < SIZE) newRow.push(0);
  return { row: newRow, scoreGained };
};

const App = () => {
  const [grid, setGrid] = useState(() =>
    addRandomTile(addRandomTile(generateEmptyGrid()))
  );
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(0);

  const restartGame = () => {
    setGrid(addRandomTile(addRandomTile(generateEmptyGrid())));
    setScore(0);
  };

  const handleMove = (direction) => {
    let moved = false;
    let newScore = 0;

    const applyOperation = (g) => {
      return g.map((row) => {
        const { row: newRow, scoreGained } = operateRow(row);
        newScore += scoreGained;
        return newRow;
      });
    };

    let newGrid = [...grid];

    if (direction === "ArrowLeft") {
      newGrid = applyOperation(newGrid);
    } else if (direction === "ArrowRight") {
      newGrid = reverse(applyOperation(reverse(newGrid)));
    } else if (direction === "ArrowUp") {
      newGrid = transpose(applyOperation(transpose(newGrid)));
    } else if (direction === "ArrowDown") {
      newGrid = transpose(reverse(applyOperation(reverse(transpose(newGrid)))));
    }

    if (JSON.stringify(newGrid) !== JSON.stringify(grid)) {
      moved = true;
    }

    if (moved) {
      const updatedGrid = addRandomTile(newGrid);
      setGrid(updatedGrid);
      setScore((prev) => {
        const newTotal = prev + newScore;
        if (newTotal > highScore) {
          setHighScore(newTotal);
        }
        return newTotal;
      });

      if (isGameOver(updatedGrid)) {
        setTimeout(() => {
          alert("Game Over! Click Restart to play again.");
        }, 100);
      }
    }
  };

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (["ArrowUp", "ArrowDown", "ArrowLeft", "ArrowRight"].includes(e.key)) {
        e.preventDefault();
        handleMove(e.key);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [grid]);

  return (
    <div className="App">
      <div className="title">
        <h1>2048 Game</h1>
        <button className="restart-btn" onClick={restartGame}>
          Restart
        </button>
      </div>

      <div className="grid">
        {grid.map((row, i) => (
          <div className="row" key={i}>
            {row.map((cell, j) => (
              <div className="cell" key={j}>
                {cell !== 0 ? cell : ""}
              </div>
            ))}
          </div>
        ))}
      </div>
      <div className="score">
        <p>Score: {score}</p>
        <p>High Score: {highScore}</p>
      </div>

      <div className="foot">
        <p>This website is developed by Sagar Kumar</p>
      </div>
    </div>
  );
};

export default App;
